function CircularStructureStringify(object) {
    var cache = [];
    var str = JSON.stringify(object,
    
        function(key, value) {
            if (typeof value === 'object' && value !== null) {
                if (cache.indexOf(value) !== -1) {
                    return;
                }
               
                cache.push(value);
            }
            return value;
        }, 4);
    cache = null; 
    return str;
};

module.exports = CircularStructureStringify;